parameters.jsCode:
const processedItems = [];
for (const item of $input.item) {
  let textContent = '';
  let source = 'Unknown';
  let url = '';
  let title = '';

  // Example for HTTP Request output (assuming data is in 'item.json.data')
  if (item.json && item.json.data && Array.isArray(item.json.data.results)) {
    for (const result of item.json.data.results) {
      if (result.html) { // Assuming scraper returns HTML
        // Simple text extraction (consider a more robust library like 'cheerio' if needed)
        // const cheerio = require('cheerio'); // Requires custom n8n setup if not bundled
        // const $ = cheerio.load(result.html);
        // textContent = $('body').text().slice(0, 4000); // Limit text length for AI
        textContent = result.html.replace(/<[^>]*>/g, '').slice(0, 4000); // Basic HTML stripping
      } else if (result.text) { // Assuming scraper returns plain text
        textContent = result.text.slice(0, 4000);
      }
      title = result.title || '';
      url = result.url || '';
      source = result.source || 'Web Scraper Data';

      if (textContent.length > 50) { // Only process meaningful content
        processedItems.push({
          json: {
            content: textContent,
            title: title,
            url: url,
            source: source
          }
        });
      }
    }
  }

  // Example for RSS Feed output
  if (item.json.description && item.json.title && item.json.link) {
    textContent = item.json.description.slice(0, 4000);
    title = item.json.title;
    url = item.json.link;
    source = 'RSS Feed';
     if (textContent.length > 50) {
        processedItems.push({
          json: {
            content: textContent,
            title: title,
            url: url,
            source: source
          }
        });
    }
  }
}
return processedItems;

