// Initialize an empty string to store all title, url, and description pairs
let aggregatedOutputText = "";

// Loop through all items passed to this Function node
for (let item of items) {
  // Access the JSON data from "Query 1" node for the current item
  const queryData = item.json;

  // Ensure there is a "web.results" array to process
  if (queryData.web?.results && Array.isArray(queryData.web.results)) {
    // Loop through all results in the "web.results" array
    for (let result of queryData.web.results) {
      // Extract the title, url, and description for each result
      const title = result.title || "No Title";
      const url = result.url || "No URL";
      const description = result.description || "No Description";

      // Append the values to the aggregated string
      aggregatedOutputText += `Title: ${title}\nURL: ${url}\nDescription: ${description}\n\n`;
    }
  } else {
    // If no results array, handle gracefully
    aggregatedOutputText += "No results found for this item.\n\n";
  }
}

// Trim the final string to remove any trailing newline and whitespace
aggregatedOutputText = aggregatedOutputText.trim();

// Return a single item containing the aggregated output as a string
return [
  {
    json: {
      aggregated_text: aggregatedOutputText
    }
  }
];

