// Initialize an empty string to store the final aggregated output
let aggregatedOutputText = "";

// Loop through all items passed from the Loop node
for (let i = 0; i < items.length; i++) {
  // Extract data for the current item
  const currentItem = items[i].json.data;
  const itemNumber = i + 1; // Item number for readability
  const url = currentItem.url || "No URL";
  const title = currentItem.title || "No Title";
  const description = currentItem.description || "No Description";
  const content = currentItem.content || "No Content";

  // Append formatted data for this item to the aggregated output
  aggregatedOutputText += `Item ${itemNumber}: Title: ${title}, URL: ${url}, Description: ${description}, Content: ${content} || `;
}

// Trim any trailing separators (if necessary)
aggregatedOutputText = aggregatedOutputText.trim();

// Return a single output containing the aggregated data as a single-line string
return [
  {
    json: {
      aggregated_text: aggregatedOutputText
    }
  }
];
