options.customCss:
:root {
	--font-family: 'Open Sans', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;
	--font-size-body: 12px;
	--font-size-label: 14px;
	--font-size-test-notice: 12px;
	--font-size-input: 14px;
	--font-size-header: 20px;
	--font-size-paragraph: 14px;
	--font-size-link: 12px;
	--font-size-error: 12px;
	--font-size-html-h1: 28px;
	--font-size-html-h2: 20px;
	--font-size-html-h3: 16px;
	--font-size-html-h4: 14px;
	--font-size-html-h5: 12px;
	--font-size-html-h6: 10px;
	--font-size-subheader: 14px;

	/* Colors */
	--color-background: #fbfcfe;
	--color-test-notice-text: #e6a23d;
	--color-test-notice-bg: #fefaf6;
	--color-test-notice-border: #f6dcb7;
	--color-card-bg: #ffffff;
	--color-card-border: #dbdfe7;
	--color-card-shadow: rgba(99, 77, 255, 0.06);
	--color-link: #7e8186;
	--color-header: #525356;
	--color-label: #555555;
	--color-input-border: #dbdfe7;
	--color-input-text: #71747A;
	--color-focus-border: rgb(90, 76, 194);
	--color-submit-btn-bg: #0000FF; /* Changed to blue */
	--color-submit-btn-text: #ffffff;
	--color-error: #ea1f30;
	--color-required: #ff6d5a;
	--color-clear-button-bg: #7e8186;
	--color-html-text: #555;
	--color-html-link: #ff6d5a;
	--color-header-subtext: #7e8186;

	/* Border Radii */
	--border-radius-card: 8px;
	--border-radius-input: 6px;
	--border-radius-clear-btn: 50%;
	--card-border-radius: 8px;

	/* Spacing */
	--padding-container-top: 24px;
	--padding-card: 24px;
	--padding-test-notice-vertical: 12px;
	--padding-test-notice-horizontal: 24px;
	--margin-bottom-card: 16px;
	--padding-form-input: 12px;
	--card-padding: 24px;
	--card-margin-bottom: 16px;

	/* Dimensions */
	--container-width: 448px;
	--submit-btn-height: 48px;
	--checkbox-size: 18px;

	/* Others */
	--box-shadow-card: 0px 4px 16px 0px var(--color-card-shadow);
	--opacity-placeholder: 0.5;
}

parameters.text:
=Evaluate the candidate for the following job:

--- Job Description ---
Position: {{ $('Download file').item.json['Job Role'] }}
Job Description: {{ $('Extract Job Description').item.json.text }}
------------------------

Below is the extracted candidate profile:
{{ $('Extract profile').item.json.text }}

parameters.jsCode:
const evalResult = $input.first().json.output
const profile = $('Profile Analyzer Agent').first().json.output;
return [
  {
    json: {
      full_name:profile.full_name,
      email: profile.contact.email,
      phone: profile.contact.phone,
      position: $('Application form').first().json['Job Role'],
      overall_fit_score: evalResult.overall_fit_score,
      recommendation: evalResult.recommendation,
      strengths: evalResult.strengths.join(', '),
      concerns_or_gaps: evalResult.concerns_or_gaps.join(', '),
      final_notes: evalResult.final_notes,
      submitted_date: $now.toFormat("yyyyLLdd-HHmmss")
    }
  }
];

parameters.contentValue:
=<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <title>Application Update</title>
  <style>
    body {
      font-family: Arial, sans-serif;
      background-color: #f4f6f9;
      color: #333;
      padding: 20px;
    }
    .container {
      max-width: 600px;
      background-color: #ffffff;
      margin: auto;
      padding: 30px;
      border-radius: 8px;
      box-shadow: 0 0 10px rgba(0,0,0,0.05);
    }
    .header {
      font-size: 20px;
      font-weight: bold;
      color: #2b5cb8;
      margin-bottom: 20px;
    }
    .footer {
      font-size: 12px;
      color: #999;
      text-align: center;
      margin-top: 40px;
    }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">Application Update</div>

    <p>Hi {{ $json.full_name }},</p>

    <p>Thank you for applying for the <strong>{{ $json.position }}</strong> position.</p>

    <p>We appreciate your interest and the time you took to apply. After careful review, we’ve decided not to move forward with your application at this time.</p>

    <p>We wish you the best in your job search and future opportunities.</p>

    <p>Kind regards,<br>
    Recruitment Team</p>

    <div class="footer">
      This is an automated message. Please do not reply.
    </div>
  </div>
</body>
</html>
