// Retrieve the JSON from the previous node
const input = items[0].json;

// Extract the citations array; default to an empty array if not present
const citations = input.citations || [];

// Build an HTML unordered list for the citations
let citationsHTML = '<ul>';
if (Array.isArray(citations)) {
  citations.forEach(citation => {
    citationsHTML += `<li><a href="${citation}" target="_blank">${citation}</a></li>`;
  });
}
citationsHTML += '</ul>';

// Return a new JSON object containing the generated HTML snippet
return [{ json: { citationsHTML } }];

const input = $node["Scrape Profiles + Posts - Relevance AI"].json;

// Getting posts from output, then fallback to top-level.
const posts = (input.output && input.output.last_30_days_posts_transformed) 
  || input.last_30_days_posts_transformed 
  || [];

// Build HTML for each LinkedIn post
let postsHTML = '';
if (Array.isArray(posts)) {
  posts.forEach(post => {
    postsHTML += `<div class="linkedin-post" style="border:1px solid #ddd; padding:10px; margin-bottom:10px;">
      <p>${post.user_post}</p>
      <p><em>Posted on: ${post.posted}</em></p>
    </div>`;
  });
}

// Return only the generated HTML
return [{ json: { postsHTML } }];

// Get the input data from the first item
const input = $node["Scrape Profiles + Posts - Relevance AI"].json;

// Navigate to the experiences array within the "output" object
const experiences = input.linkedin_profile_details_data?.experiences || [];
// Build the HTML table rows string
let htmlRows = '';
experiences.forEach(exp => {
  htmlRows += `<tr>
      <td>${exp.company}</td>
      <td>${exp.title}</td>
      <td>${exp.date_range}</td>
      <td>${exp.location}</td>
    </tr>`;
});

// Return a new object that only contains the experiencesTable field
return [{ json: { experiencesTable: htmlRows } }];

// Get the input data from the first item
const input = $node["Scrape Profiles + Posts - Relevance AI"].json;

// Navigate to the educations array in the output object
const educations = input.linkedin_profile_details_data?.educations || [];
// Build the HTML table rows for education
let educationRows = '';
educations.forEach(edu => {
  educationRows += `<tr>
      <td>${edu.school}</td>
      <td>${edu.degree}</td>
      <td>${edu.field_of_study}</td>
    </tr>`;
});

// Return a new object that only contains the educationTable key
return [{ json: { educationTable: educationRows } }];
