// Advanced Sales Pipeline Automation & Lead Intelligence Processing
const inputData = $input.all();
const processedLeads = [];

// Configuration for lead scoring and qualification
const salesConfig = {
  leadScoringWeights: {
    company_size: {
      '1-10 employees': 10,
      '11-50 employees': 25,
      '51-200 employees': 40,
      '201-500 employees': 60,
      '501-1000 employees': 80,
      '1000+ employees': 100
    },
    industry_fit: {
      'Technology': 90,
      'Software': 95,
      'Financial Services': 85,
      'Healthcare': 80,
      'Manufacturing': 70,
      'Retail': 60,
      'Education': 50,
      'Non-profit': 20
    },
    funding_stage: {
      'Seed': 30,
      'Series A': 50,
      'Series B': 70,
      'Series C+': 85,
      'IPO': 90,
      'Profitable': 95
    },
    lead_source: {
      'Website Form': 60,
      'Demo Request': 90,
      'Webinar': 70,
      'Content Download': 40,
      'Referral': 85,
      'Cold Outreach': 20,
      'Trade Show': 75,
      'Partner': 80
    },
    job_title: {
      'CEO': 95, 'CTO': 90, 'VP': 85, 'Director': 75,
      'Manager': 60, 'Coordinator': 40, 'Specialist': 50,
      'Analyst': 45, 'Assistant': 25
    }
  },
  idealCustomerProfile: {
    min_employees: 50,
    target_industries: ['Technology', 'Software', 'Financial Services'],
    min_funding: 5000000, // $5M
    target_titles: ['CEO', 'CTO', 'VP', 'Director'],
    priority_sources: ['Demo Request', 'Referral', 'Website Form']
  },
  automationTriggers: {
    high_score_threshold: 70,
    enterprise_threshold: 85,
    immediate_follow_up_threshold: 90
  }
};

// Advanced lead scoring algorithm
function calculateLeadScore(lead, companyData, financialData) {
  let totalScore = 0;
  const scoringDetails = [];

  // Company size scoring
  const companySize = companyData?.company_size || lead.company_size || 'Unknown';
  const companySizeScore = salesConfig.leadScoringWeights.company_size[companySize] || 15;
  totalScore += companySizeScore;
  scoringDetails.push({ factor: 'Company Size', value: companySize, score: companySizeScore });

  // Industry fit scoring
  const industry = companyData?.industry || lead.industry || 'Unknown';
  const industryScore = salesConfig.leadScoringWeights.industry_fit[industry] || 30;
  totalScore += industryScore;
  scoringDetails.push({ factor: 'Industry Fit', value: industry, score: industryScore });

  // Funding stage scoring
  const fundingStage = financialData?.last_funding_round || 'Unknown';
  const fundingScore = salesConfig.leadScoringWeights.funding_stage[fundingStage] || 25;
  totalScore += fundingScore;
  scoringDetails.push({ factor: 'Funding Stage', value: fundingStage, score: fundingScore });

  // Lead source scoring
  const leadSource = lead.lead_source || 'Unknown';
  const sourceScore = salesConfig.leadScoringWeights.lead_source[leadSource] || 30;
  totalScore += sourceScore;
  scoringDetails.push({ factor: 'Lead Source', value: leadSource, score: sourceScore });

  // Job title scoring
  const jobTitle = lead.job_title || '';
  let titleScore = 30; // default
  Object.entries(salesConfig.leadScoringWeights.job_title).forEach(([title, score]) => {
    if (jobTitle.toLowerCase().includes(title.toLowerCase())) {
      titleScore = Math.max(titleScore, score);
    }
  });
  totalScore += titleScore;
  scoringDetails.push({ factor: 'Job Title', value: jobTitle, score: titleScore });

  // Engagement and intent signals
  let engagementScore = 0;
  if (lead.lead_source === 'Demo Request') engagementScore += 20;
  if (lead.notes && lead.notes.toLowerCase().includes('enterprise')) engagementScore += 15;
  if (lead.notes && lead.notes.toLowerCase().includes('urgent')) engagementScore += 10;
  if (lead.tags && lead.tags.includes('high-intent')) engagementScore += 15;

  totalScore += engagementScore;
  scoringDetails.push({ factor: 'Engagement Signals', value: 'Multiple factors', score: engagementScore });

  // Company traction scoring
  let tractionScore = 0;
  if (financialData?.total_funding) {
    const fundingAmount = parseFloat(financialData.total_funding.replace(/[^0-9.]/g, '')) * 1000000;
    if (fundingAmount > 50000000) tractionScore += 25;
    else if (fundingAmount > 10000000) tractionScore += 20;
    else if (fundingAmount > 5000000) tractionScore += 15;
    else if (fundingAmount > 1000000) tractionScore += 10;
  }

  if (companyData?.recent_posts && companyData.recent_posts.length > 0) {
    tractionScore += 10; // Active on social media
  }

  totalScore += tractionScore;
  scoringDetails.push({ factor: 'Company Traction', value: 'Funding + Activity', score: tractionScore });

  return {
    total_score: Math.min(totalScore, 100), // Cap at 100
    scoring_details: scoringDetails,
    score_breakdown: {
      company_size: companySizeScore,
      industry_fit: industryScore,
      funding_stage: fundingScore,
      lead_source: sourceScore,
      job_title: titleScore,
      engagement: engagementScore,
      traction: tractionScore
    }
  };
}

// Lead qualification and categorization
function qualifyLead(lead, leadScore, companyData, financialData) {
  const qualification = {
    grade: 'C', // Default
    category: 'Standard',
    priority: 'Low',
    fit_assessment: [],
    red_flags: [],
    opportunity_indicators: []
  };

  // Grade assignment based on score
  if (leadScore.total_score >= 85) qualification.grade = 'A+';
  else if (leadScore.total_score >= 75) qualification.grade = 'A';
  else if (leadScore.total_score >= 65) qualification.grade = 'B+';
  else if (leadScore.total_score >= 55) qualification.grade = 'B';
  else if (leadScore.total_score >= 45) qualification.grade = 'C+';

  // Category and priority assignment
  if (leadScore.total_score >= salesConfig.automationTriggers.enterprise_threshold) {
    qualification.category = 'Enterprise';
    qualification.priority = 'Critical';
  } else if (leadScore.total_score >= salesConfig.automationTriggers.high_score_threshold) {
    qualification.category = 'High-Value';
    qualification.priority = 'High';
  } else if (leadScore.total_score >= 50) {
    qualification.category = 'Qualified';
    qualification.priority = 'Medium';
  } else {
    qualification.category = 'Nurture';
    qualification.priority = 'Low';
  }

  // Fit assessment
  if (companyData?.company_size && companyData.company_size.includes('200+')) {
    qualification.fit_assessment.push('Target company size');
  }

  if (salesConfig.idealCustomerProfile.target_industries.includes(companyData?.industry)) {
    qualification.fit_assessment.push('Target industry match');
  }

  if (financialData?.last_funding_round && ['Series B', 'Series C+', 'IPO'].includes(financialData.last_funding_round)) {
    qualification.fit_assessment.push('Well-funded company');
  }

  // Red flags detection
  if (companyData?.company_size && companyData.company_size.includes('1-10')) {
    qualification.red_flags.push('Very small company');
  }

  if (lead.email && lead.email.includes('gmail.com')) {
    qualification.red_flags.push('Personal email address');
  }

  if (!companyData?.website && !lead.website) {
    qualification.red_flags.push('No company website');
  }

  // Opportunity indicators
  if (lead.lead_source === 'Demo Request') {
    qualification.opportunity_indicators.push('Requested product demo');
  }

  if (lead.notes && lead.notes.toLowerCase().includes('budget')) {
    qualification.opportunity_indicators.push('Mentioned budget');
  }

  if (companyData?.recent_posts && companyData.recent_posts.some(post => 
    post.title.toLowerCase().includes('hiring') || post.title.toLowerCase().includes('expansion'))) {
    qualification.opportunity_indicators.push('Company in growth phase');
  }

  return qualification;
}

// Sales automation recommendations
function generateSalesActions(lead, qualification, leadScore) {
  const actions = {
    immediate_actions: [],
    follow_up_sequence: [],
    sales_rep_assignment: 'Auto-assign',
    meeting_priority: 'Standard',
    email_template: 'standard_intro',
    calendar_booking_type: 'discovery_call'
  };

  // Immediate actions based on score and qualification
  if (leadScore.total_score >= salesConfig.automationTriggers.immediate_follow_up_threshold) {
    actions.immediate_actions.push('Send immediate personal email');
    actions.immediate_actions.push('Schedule same-day call');
    actions.sales_rep_assignment = 'Senior AE';
    actions.meeting_priority = 'Urgent';
    actions.email_template = 'high_value_intro';
  } else if (leadScore.total_score >= salesConfig.automationTriggers.enterprise_threshold) {
    actions.immediate_actions.push('Add to priority sequence');
    actions.immediate_actions.push('Research company background');
    actions.sales_rep_assignment = 'Enterprise AE';
    actions.meeting_priority = 'High';
    actions.email_template = 'enterprise_intro';
  } else if (leadScore.total_score >= salesConfig.automationTriggers.high_score_threshold) {
    actions.immediate_actions.push('Add to qualified lead sequence');
    actions.sales_rep_assignment = 'Standard AE';
    actions.email_template = 'qualified_intro';
  } else {
    actions.immediate_actions.push('Add to nurture sequence');
    actions.sales_rep_assignment = 'Junior AE';
    actions.email_template = 'nurture_intro';
  }

  // Follow-up sequence generation
  if (qualification.category === 'Enterprise') {
    actions.follow_up_sequence = [
      'Day 1: Personal welcome email + calendar invite',
      'Day 2: Follow-up call attempt',
      'Day 3: Executive summary email',
      'Day 5: Case study sharing',
      'Day 7: Executive meeting proposal'
    ];
  } else if (qualification.category === 'High-Value') {
    actions.follow_up_sequence = [
      'Day 1: Welcome email with demo offer',
      'Day 3: Follow-up call',
      'Day 5: Product demo scheduling',
      'Day 10: ROI calculator sharing'
    ];
  } else {
    actions.follow_up_sequence = [
      'Day 1: Welcome email',
      'Day 7: Educational content',
      'Day 14: Industry insights',
      'Day 21: Product webinar invitation'
    ];
  }

  return actions;
}

// Main processing logic
for (const data of inputData) {
  try {
    const lead = data.leads?.[0] || data;
    const companyData = data.company_data || {};
    const financialData = data.financial_data || {};

    // Calculate lead score
    const leadScore = calculateLeadScore(lead, companyData, financialData);

    // Qualify lead
    const qualification = qualifyLead(lead, leadScore, companyData, financialData);

    // Generate sales actions
    const salesActions = generateSalesActions(lead, qualification, leadScore);

    // Create comprehensive lead record
    const processedLead = {
      ...lead,
      lead_score: leadScore.total_score,
      scoring_details: leadScore.scoring_details,
      score_breakdown: leadScore.score_breakdown,
      qualification: qualification,
      sales_actions: salesActions,
      processing_timestamp: new Date().toISOString(),
      enriched_data: {
        company: companyData,
        financial: financialData
      }
    };

    processedLeads.push(processedLead);
  } catch (error) {
    console.error('Error processing lead:', error);
  }
}

return processedLeads;
