// Get existing data from Excel
const existingData = $input.all();

// Get new application data from webhook
const newApplication = $('Webhook - Application Form').first().json;

// Create new student record
const newStudent = {
  'Application ID': 'APP-' + Date.now(),
  'First Name': newApplication.firstName,
  'Last Name': newApplication.lastName,
  'Email': newApplication.email,
  'Phone': newApplication.phone || '',
  'Program Interest': newApplication.program || 'General',
  'Grade Level': newApplication.gradeLevel || '',
  'School': newApplication.school || '',
  'Guardian Name': newApplication.guardianName || '',
  'Guardian Phone': newApplication.guardianPhone || '',
  'Application Date': new Date().toISOString().split('T')[0],
  'Status': 'Pending Review',
  'Notes': newApplication.additionalNotes || ''
};

// Combine existing data with new application
const allData = [...existingData, newStudent];

return allData.map(item => ({ json: item }));

// Get application data
const applicationData = $('Webhook - Application Form').first().json;

// Create email content
const emailSubject = 'Welcome! Your Application Has Been Received';
const emailBody = `Dear ${applicationData.firstName} ${applicationData.lastName},

Thank you for your interest in our program! We have successfully received your application and it is currently under review.

Application Details:
- Program of Interest: ${applicationData.program || 'General Program'}
- Grade Level: ${applicationData.gradeLevel || 'Not Specified'}
- Application Date: ${new Date().toLocaleDateString()}

What happens next?
1. Our admissions team will review your application within 3-5 business days
2. You will receive an email with next steps or any additional requirements
3. If you have any questions, please don't hesitate to contact us

Thank you for choosing our institution. We look forward to potentially welcoming you to our community!

Best regards,
Admissions Team`;

return [{
  json: {
    to: applicationData.email,
    subject: emailSubject,
    body: emailBody,
    studentName: `${applicationData.firstName} ${applicationData.lastName}`,
    program: applicationData.program,
    applicationDate: new Date().toLocaleDateString()
  }
}];
