parameters.jsCode:
// Get the Gmail thread data from the previous node
const threadData = $input.all();

// Array to store all messages
const allMessages = [];

// Function to extract text from message payload
function extractMessageBody(payload) {
  let body = '';

  if (payload.body && payload.body.data) {
    // Single part message
    body = Buffer.from(payload.body.data, 'base64').toString('utf-8');
  } else if (payload.parts) {
    // Multi-part message
    for (const part of payload.parts) {
      if (part.mimeType === 'text/plain' && part.body && part.body.data) {
        body += Buffer.from(part.body.data, 'base64').toString('utf-8');
      } else if (part.mimeType === 'text/html' && part.body && part.body.data && !body) {
        // Use HTML if no plain text found
        body = Buffer.from(part.body.data, 'base64').toString('utf-8');
      } else if (part.parts) {
        // Nested parts (recursive)
        body += extractMessageBody(part);
      }
    }
  }

  return body.trim();
}

// Loop through each item from the Gmail node
for (const item of threadData) {
  // Check if messages exist in the item
  if (item.json.messages && Array.isArray(item.json.messages)) {
    // Loop through each message in the thread
    for (let i = 0; i < item.json.messages.length; i++) {
      const message = item.json.messages[i];

      // Extract the full message body
      const fullBody = message.payload ? extractMessageBody(message.payload) : message.snippet || '';

      if (fullBody) {
        allMessages.push({
          messageIndex: i,
          threadId: item.json.id || 'unknown',
          snippet: message.snippet || '',
          fullBody: fullBody,
          messageId: message.id || 'unknown',
          subject: message.payload?.headers?.find(h => h.name === 'Subject')?.value || '',
          from: message.payload?.headers?.find(h => h.name === 'From')?.value || '',
          date: message.payload?.headers?.find(h => h.name === 'Date')?.value || ''
        });
      }
    }
  }
}

// Return all messages as a single item with an array
return [{
  json: {
    threadId: threadData[0]?.json?.id || 'unknown',
    totalMessages: allMessages.length,
    messages: allMessages
  }
}];

