const html = $json["data"];
const ig = html.match(/https?:\/\/(www\.)?instagram\.com\/[^\s"']+/i);
const tiktok = html.match(/https?:\/\/(www\.)?tiktok\.com\/[^\s"']+/i);

return [{
  json: {
    instagram: ig ? ig[0] : null,
    tiktok: tiktok ? tiktok[0] : null
  }
}];

const html = $json["data"] || "";

// Fungsi bantu untuk ekstrak teks antar tag
function extractTag(tag, html) {
  const regex = new RegExp(`<${tag}[^>]*>(.*?)<\\/${tag}>`, 'gis');
  const matches = [];
  let match;
  while ((match = regex.exec(html)) !== null) {
    const clean = match[1].replace(/<[^>]*>/g, '').trim();
    if (clean.length > 5) matches.push(clean);
  }
  return matches;
}

// Fungsi untuk ambil meta tag
function extractMeta(name, html) {
  const regex = new RegExp(`<meta[^>]+name=["']${name}["'][^>]+content=["']([^"']+)["']`, 'i');
  const match = regex.exec(html);
  return match ? match[1].trim() : '';
}

// Ambil bagian penting
const title = extractTag('title', html)[0] || '';
const description = extractMeta('description', html);
const keywords = extractMeta('keywords', html);
const headings = extractTag('h1', html).concat(extractTag('h2', html), extractTag('h3', html), extractTag('h4', html));
const paragraphs = extractTag('p', html).filter(p => p.length > 30 && p.length < 300);
const buttons = extractTag('button', html).concat(extractTag('a', html)).filter(t => /order|buy|join|shop|sign|book|start|contact|subscribe/i.test(t));

// Hasil akhir
const summary = [
  `🔹 Title:\n${title}`,
  `🔹 Description:\n${description}`,
  `🔹 Keywords:\n${keywords}`,
  `🔹 Headings:\n${headings.slice(0, 5).join('\n') || '(Tidak ditemukan)'}`,
  `🔹 Paragraf Penting:\n${paragraphs.slice(0, 3).join('\n\n') || '(Tidak ditemukan)'}`,
  `🔹 CTA / Tombol:\n${buttons.slice(0, 5).join('\n') || '(Tidak ditemukan)'}`
].join('\n\n');

return [{ json: { simplified_summary: summary } }];
