// Mengambil data event dari input
const event = $input.item.json.body.event;

// Fungsi baru untuk mengekstrak DOMAIN dari dalam field 'name'
const extractDomainFromName = (nameField) => {
  if (!nameField || typeof nameField !== 'string') { return null; }
  // Memecah teks berdasarkan karakter '"'
  const parts = nameField.split('"');
  // Domain biasanya adalah bagian kedua (indeks 1)
  if (parts.length >= 2) {
    const potentialDomain = parts[1];
    // Cek sederhana, jika mengandung titik, kita anggap itu domain
    if (potentialDomain.includes('.')) {
      return potentialDomain;
    }
  }
  return null;
};

// Coba ekstrak domain dari field 'name'
const domainFromName = extractDomainFromName(event.name);

let finalUrl = "";
let indicatorType = "";
let indicatorValue = "";

// --- LOGIKA PRIORITAS BARU ---

// 1. Cek hash file
if (event?.data?.sha256) {
  indicatorType = "file";
  indicatorValue = event.data.sha256;
  finalUrl = `https://www.virustotal.com/api/v3/files/${indicatorValue}`;
} 
// 2. Cek DOMAIN (bukan URL lagi)
else if (domainFromName) {
  indicatorType = "domain";
  indicatorValue = domainFromName;
  // Menggunakan endpoint DOMAIN di VirusTotal
  finalUrl = `https://www.virustotal.com/api/v3/domains/${indicatorValue}`;
}
// 3. Cek Alamat IP
else if (event?.source_ip) {
  indicatorType = "ip";
  indicatorValue = event.source_ip;
  finalUrl = `https://www.virustotal.com/api/v3/ip_addresses/${indicatorValue}`;
}

// Pastikan kita punya URL untuk diperiksa
if (!finalUrl) {
  throw new Error("Tidak ada indikator (sha256, domain, atau source_ip) yang ditemukan.");
}

// Kembalikan objek terstruktur untuk digunakan di node selanjutnya
return {
  url_to_check: finalUrl,
  indicator_type: indicatorType,
  indicator_value: indicatorValue
};

// KODE UNTUK NODE "Format Hasil VT"

// Mengambil output JSON dari node VirusTotal sebelumnya
const vtData = $input.item.json;
const detailedResults = vtData.data?.attributes?.last_analysis_results || {};

const flaggingVendors = [];
let totalFlags = 0;

// Loop melalui setiap hasil vendor
for (const vendor in detailedResults) {
  const result = detailedResults[vendor];
  // Cek jika kategori-nya berbahaya atau mencurigakan
  if (result.category === 'malicious' || result.category === 'suspicious') {
    totalFlags++;
    // Format: - `NamaVendor`: *malicious*
    flaggingVendors.push(`- \`${vendor}\`: *${result.category}*`);
  }
}

// Buat teks daftar vendor, atau pesan default jika kosong
const vendorsListText = flaggingVendors.length > 0
  ? flaggingVendors.join('\n')
  : "Tidak ada vendor yang menandai sebagai berbahaya.";

// Buat ringkasan singkat untuk dikirim ke Gemini
const reputationSummaryForGemini = `Indikator ini ditandai sebagai berbahaya/mencurigakan oleh ${totalFlags} vendor keamanan di VirusTotal.`;

// Kembalikan semua data yang sudah diformat
return {
  vendorsListText: vendorsListText,
  reputationSummaryForGemini: reputationSummaryForGemini,
  totalFlags: totalFlags
};
