return items.map(item => {
  const summary = item.json;
  return {
    json: {
      vendor: summary.vendor || 'Unknown',
      price: summary.price || 0,
      deliveryTime: summary.deliveryTime || 'N/A',
      features: summary.features || []
    }
  };
});

