parameters.inputSchema:
{
  "type": "array",
  "items": {
    "type": "object",
    "properties": {
      "copy_number": {
        "type": "integer",
        "description": "Sequential number identifying this copy variant"
      },
      "target_persona": {
        "type": "object",
        "properties": {
          "label": {
            "type": "string",
            "description": "The persona label from input_persona_json"
          },
          "maslow_category": {
            "type": "string",
            "description": "The Maslow hierarchy category for this persona"
          }
        },
        "required": ["label", "maslow_category"],
        "additionalProperties": false
      },
      "selected_headline": {
        "type": "string",
        "description": "The headline chosen from input_headline_packages_json for this copy"
      },
      "advertorial_copy": {
        "type": "object",
        "description": "The rewritten advertorial content maintaining exact schema from input_advertorial_json",
        "additionalProperties": true
      }
    },
    "required": ["copy_number", "target_persona", "selected_headline", "advertorial_copy"],
    "additionalProperties": false
  },
  "minItems": 1,
  "maxItems": 20,
  "description": "Array of advertorial copies, each optimized for different persona-headline combinations"
}

parameters.jsCode:
const output = $input.all()[0].json.output;

const splitOutput = output.split("\n\n## ");
const marketingAngles = splitOutput[1]
  .replace("1. Marketing Angles\n- ", "")
  .split("\n- ");
const customerMotivations = splitOutput[2]
  .replace("2. Customer Motivations\n- ", "")
  .split("\n- ");
const frictionsBarriers = splitOutput[3]
  .replace("3. Frictions & Barriers\n- ", "")
  .split("\n- ");
const productOpportunities = splitOutput[4]
  .replace("4. Product Opportunities\n- ", "")
  .split("\n- ");
const voiceOfCustomer = splitOutput[5]
  .replace("5. Voice of Customer (VOC) Snippets\n- ", "")
  .split("\n- ");

return {
  "Marketing Angles": marketingAngles,
  "Customer Motivations": customerMotivations,
  "Frictions & Barriers": frictionsBarriers,
  "Product Opportunities": productOpportunities,
  "Voice of Customer (VOC) Snippets": voiceOfCustomer,
};

