<body>
    <!-- Navigation Menu -->
    <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
        <div class="container-fluid">
            <a class="navbar-brand" href="#">POS SYSTEM</a>
        </div>
    </nav>

    <div class="main-container">
        <!-- Left Panel - Items Selection -->
        <div class="left-panel">
            <div class="header-section">
                <div class="category-header">
                    <h1 class="category-title">POS SYSTEM</h1>
                </div>

                <div class="search-filter">
                    <div class="search-box">
                        <input type="text" id="searchInput" placeholder="Search products..." onkeyup="filterProducts()">
                        <i class="fas fa-search"></i>
                    </div>
                </div>

                <div class="category-tabs" id="categoryTabs">
                    <!-- Categories will be dynamically generated from JSON data -->
                </div>
            </div>

            <div class="products-grid" id="productsGrid">
                <!-- Products will be dynamically generated from JSON data -->
            </div>
        </div>

        <!-- Right Panel - Current Order -->
        <div class="right-panel">
            <h2 class="order-header">Current Order</h2>

            <div class="customer-form">
                <h4 class="form-title">Customer Information</h4>
                <div class="form-group">
                    <label for="customerName">Name</label>
                    <input type="text" id="customerName" class="form-control" placeholder="Enter customer name"
                        required>
                </div>
            </div>

            <div class="order-items" id="orderItems">
                <!-- Order items will be dynamically added here -->
            </div>

            <div class="order-summary">
                <div class="summary-row">
                    <span>Subtotal</span>
                    <span id="subtotal">$0.00</span>
                </div>
                <div class="summary-row">
                    <span>Discount</span>
                    <span id="discount">$0.00</span>
                </div>
                <div class="summary-row total">
                    <span>Total</span>
                    <span id="total">$0.00</span>
                </div>
            </div>

            <form action="{{ $resumeWebhookUrl }}" method="post" id="orderForm">
                <!-- Hidden inputs for order data -->
                <input type="hidden" name="customerName" id="hiddenCustomerName">
                <input type="hidden" name="orderItems" id="hiddenOrderItems">
                <input type="hidden" name="orderTotals" id="hiddenOrderTotals">

                <button type="submit" class="continue-btn" onclick="prepareFormData(event)">Continue</button>
            </form>
        </div>
    </div>

    <script>
        // Products data from n8n workflow - separate arrays
        const productIds = `{{ $json.productId }}`;
        const productNames = `{{ $json.productName }}`;
        const productImages = `{{ $json.productImage }}`;
        const categoryNames = `{{ $json.productCategoryName }}`;
        const prices = `{{ $json.productPriceUsd }}`;
        const discounts = `{{ $json.productDiscount }}`;

        let orderItems = [];
        let orderTotal = 0;
        let currentCategory = 'All';
        let allProducts = [];

        // Parse products data from n8n
        function initializeProducts() {
            try {
                // Parse the string arrays into actual arrays
                const parsedProductIds = JSON.parse(productIds);
                const parsedProductNames = JSON.parse(productNames);
                const parsedProductImages = JSON.parse(productImages);
                const parsedCategoryNames = JSON.parse(categoryNames);
                const parsedPrices = JSON.parse(prices);
                const parsedDiscounts = JSON.parse(discounts);

                // Combine arrays into product objects
                allProducts = parsedProductIds.map((id, index) => ({
                    'PRODUCT ID': id,
                    'PRODUCT NAME': parsedProductNames[index],
                    'PRODUCT IMAGE': parsedProductImages[index],
                    'CATEGORY NAME': parsedCategoryNames[index],
                    'PRICE (USD)': parsedPrices[index],
                    'DISCOUNT': parsedDiscounts[index]
                }));

                generateCategoryTabs();
                generateProductsGrid();
            } catch (error) {
                console.error('Error parsing products data:', error);
                // Fallback to empty array if parsing fails
                allProducts = [];
            }
        }

        function addToOrder(name, price, image, originalPrice, discountRate) {
            const existingItem = orderItems.find(item => item.name === name);

            if (existingItem) {
                existingItem.quantity += 1;
                existingItem.total = existingItem.quantity * existingItem.price;
            } else {
                orderItems.push({
                    name: name,
                    price: price,
                    originalPrice: originalPrice,
                    discountRate: discountRate,
                    image: image,
                    quantity: 1,
                    total: price
                });
            }

            updateOrderDisplay();
            calculateTotals();
        }

        function updateQuantity(name, change) {
            const item = orderItems.find(item => item.name === name);

            if (item) {
                item.quantity += change;

                if (item.quantity <= 0) {
                    orderItems = orderItems.filter(item => item.name !== name);
                } else {
                    item.total = item.quantity * item.price;
                }

                updateOrderDisplay();
                calculateTotals();
            }
        }

        function updateOrderDisplay() {
            const orderItemsContainer = document.getElementById('orderItems');
            orderItemsContainer.innerHTML = '';

            orderItems.forEach(item => {
                const itemElement = document.createElement('div');
                itemElement.className = 'order-item';

                // Calculate item discount
                const itemDiscount = (item.originalPrice * item.quantity * item.discountRate);
                const discountText = item.discountRate > 0 ? `<br><small style="color: #dc3545;">-$${itemDiscount.toFixed(2)} discount</small>` : '';

                itemElement.innerHTML = `
          <img src="${item.image}" class="item-thumbnail" alt="${item.name}">
          <div class="item-details">
            <div class="item-name">${item.name}</div>
            <div class="item-price">$${item.price.toFixed(2)}${discountText}</div>
          </div>
          <div class="item-controls">
            <button class="qty-btn" onclick="updateQuantity('${item.name}', -1)">-</button>
            <span class="qty-display">${item.quantity}</span>
            <button class="qty-btn" onclick="updateQuantity('${item.name}', 1)">+</button>
          </div>
        `;
                orderItemsContainer.appendChild(itemElement);
            });
        }

        function calculateTotals() {
            const subtotal = orderItems.reduce((sum, item) => sum + item.total, 0);

            // Calculate total discount based on individual item discounts
            const totalDiscount = orderItems.reduce((sum, item) => {
                const itemDiscount = (item.originalPrice * item.quantity * item.discountRate);
                return sum + itemDiscount;
            }, 0);

            const total = subtotal;

            document.getElementById('subtotal').textContent = `$${subtotal.toFixed(2)}`;
            document.getElementById('discount').textContent = `$${totalDiscount.toFixed(2)}`;
            document.getElementById('total').textContent = `$${total.toFixed(2)}`;
        }

        function prepareFormData(event) {
            event.preventDefault();

            // Validate customer information
            const customerName = document.getElementById('customerName').value.trim();

            if (!customerName) {
                alert('Please enter customer name.');
                document.getElementById('customerName').focus();
                return;
            }

            if (orderItems.length === 0) {
                alert('Please add items to your order first.');
                return;
            }

            // Prepare order data
            const orderData = {
                customer: {
                    name: customerName
                },
                items: orderItems,
                totals: {
                    subtotal: orderItems.reduce((sum, item) => sum + item.total, 0),
                    total: orderItems.reduce((sum, item) => sum + item.total, 0)
                }
            };

            // Set hidden form values
            document.getElementById('hiddenCustomerName').value = customerName;
            document.getElementById('hiddenOrderItems').value = JSON.stringify(orderItems);
            document.getElementById('hiddenOrderTotals').value = JSON.stringify(orderData.totals);

            // Show loading state
            const submitBtn = document.querySelector('.continue-btn');
            const originalText = submitBtn.textContent;
            submitBtn.textContent = 'Processing...';
            submitBtn.disabled = true;

            // Submit the form
            document.getElementById('orderForm').submit();
        }

        // Function to get unique categories from products data
        function getCategories() {
            const categories = [...new Set(allProducts.map(product => product['CATEGORY NAME']))];
            return ['All', ...categories];
        }

        // Function to generate category tabs
        function generateCategoryTabs() {
            const categories = getCategories();
            const categoryTabsContainer = document.getElementById('categoryTabs');
            categoryTabsContainer.innerHTML = '';

            categories.forEach(category => {
                const tab = document.createElement('button');
                tab.className = 'category-tab';
                if (category === 'All') {
                    tab.classList.add('active');
                }
                tab.textContent = category;
                tab.onclick = () => filterByCategory(category);
                categoryTabsContainer.appendChild(tab);
            });
        }

        // Function to filter products by category
        function filterByCategory(category) {
            currentCategory = category;

            // Update active tab
            document.querySelectorAll('.category-tab').forEach(tab => {
                tab.classList.remove('active');
                if (tab.textContent === category) {
                    tab.classList.add('active');
                }
            });

            // Filter products
            if (category === 'All') {
                // Re-initialize all products
                initializeProducts();
            } else {
                // Filter by category
                const parsedProductIds = JSON.parse(productIds);
                const parsedProductNames = JSON.parse(productNames);
                const parsedProductImages = JSON.parse(productImages);
                const parsedCategoryNames = JSON.parse(categoryNames);
                const parsedPrices = JSON.parse(prices);
                const parsedDiscounts = JSON.parse(discounts);

                // Combine arrays into product objects and filter by category
                allProducts = parsedProductIds.map((id, index) => ({
                    'PRODUCT ID': id,
                    'PRODUCT NAME': parsedProductNames[index],
                    'PRODUCT IMAGE': parsedProductImages[index],
                    'CATEGORY NAME': parsedCategoryNames[index],
                    'PRICE (USD)': parsedPrices[index],
                    'DISCOUNT': parsedDiscounts[index]
                })).filter(product => product['CATEGORY NAME'] === category);

                generateProductsGrid();
            }
        }

        // Function to generate product cards
        function generateProductsGrid() {
            const productsGrid = document.getElementById('productsGrid');
            productsGrid.innerHTML = '';

            allProducts.forEach(product => {
                const productCard = document.createElement('div');
                productCard.className = 'product-card';

                // Calculate final price with discount
                const originalPrice = product['PRICE (USD)'];
                const discount = product['DISCOUNT'];
                const finalPrice = originalPrice * (1 - discount);

                // Use image from JSON data
                const imageUrl = product['PRODUCT IMAGE'];

                productCard.innerHTML = `
          <img src="${imageUrl}" class="product-image" alt="${product['PRODUCT NAME']}">
          <div class="product-name">${product['PRODUCT NAME']}</div>
          <div class="product-price">
            $${finalPrice.toFixed(2)}
            ${discount > 0 ? `<span class="discount-badge">-${(discount * 100).toFixed(0)}%</span>` : ''}
          </div>
          <button class="add-btn" onclick="addToOrder('${product['PRODUCT NAME']}', ${finalPrice}, '${imageUrl}', ${originalPrice}, ${discount})">+</button>
        `;

                productsGrid.appendChild(productCard);
            });
        }

        // Function to filter products by search
        function filterProducts() {
            const searchTerm = document.getElementById('searchInput').value.toLowerCase();
            const productsGrid = document.getElementById('productsGrid');
            productsGrid.innerHTML = '';

            const filteredProducts = allProducts.filter(product =>
                product['PRODUCT NAME'].toLowerCase().includes(searchTerm) ||
                product['CATEGORY NAME'].toLowerCase().includes(searchTerm)
            );

            filteredProducts.forEach(product => {
                const productCard = document.createElement('div');
                productCard.className = 'product-card';

                // Calculate final price with discount
                const originalPrice = product['PRICE (USD)'];
                const discount = product['DISCOUNT'];
                const finalPrice = originalPrice * (1 - discount);

                // Use image from JSON data
                const imageUrl = product['PRODUCT IMAGE'];

                productCard.innerHTML = `
          <img src="${imageUrl}" class="product-image" alt="${product['PRODUCT NAME']}">
          <div class="product-name">${product['PRODUCT NAME']}</div>
          <div class="product-price">
            $${finalPrice.toFixed(2)}
            ${discount > 0 ? `<span class="discount-badge">-${(discount * 100).toFixed(0)}%</span>` : ''}
          </div>
          <button class="add-btn" onclick="addToOrder('${product['PRODUCT NAME']}', ${finalPrice}, '${imageUrl}', ${originalPrice}, ${discount})">+</button>
        `;

                productsGrid.appendChild(productCard);
            });
        }

        // Initialize the display
        initializeProducts();
        updateOrderDisplay();
        calculateTotals();
    </script>
</body>

</html>

