// Process form input and build Elasticsearch query
const formData = $input.first().json;

// Extract form values
const minAmount = formData['Minimum Amount ($)'] || 1000;
const timeRange = formData['Time Range'] || 'Last 24 Hours';
const customerId = formData['Customer ID (Optional)']?.trim() || null;
const reportFormat = formData['Report Format'] || 'Text Report';

// Convert time range to Elasticsearch format
let timeRangeES;
switch(timeRange) {
  case 'Last 1 Hour':
    timeRangeES = 'now-1h';
    break;
  case 'Last 6 Hours':
    timeRangeES = 'now-6h';
    break;
  case 'Last 24 Hours':
    timeRangeES = 'now-24h';
    break;
  case 'Last 3 Days':
    timeRangeES = 'now-3d';
    break;
  default:
    timeRangeES = 'now-24h';
}

// Build Elasticsearch query
const mustConditions = [
  {
    "range": {
      "timestamp": {
        "gte": timeRangeES
      }
    }
  },
  {
    "range": {
      "amount": {
        "gte": minAmount
      }
    }
  }
];

// Add customer filter if provided
if (customerId) {
  mustConditions.push({
    "term": {
      "customer_id.keyword": customerId
    }
  });
}

const esQuery = {
  "query": {
    "bool": {
      "must": mustConditions
    }
  },
  "sort": [{ "timestamp": { "order": "desc" } }],
  "size": 100
};

return {
  elasticsearchQuery: esQuery,
  searchParams: {
    minAmount,
    timeRange,
    customerId,
    reportFormat
  }
};

// Get data from previous nodes
const esResponse = $input.first().json;
const searchParams = $('Build Search Query').first().json.searchParams;

// Extract results
const hits = esResponse.hits?.hits || [];
const totalFound = esResponse.hits?.total?.value || 0;

// Generate filename
const timestamp = new Date().toISOString().split('T')[0];
const isCSV = searchParams.reportFormat === 'CSV Export';
const filename = `report_${timestamp}.${isCSV ? 'csv' : 'txt'}`;
const mimeType = isCSV ? 'text/csv' : 'text/plain';

// Generate report content
let reportContent = '';

if (isCSV) {
  // CSV format
  reportContent = 'Transaction_ID,Customer_ID,Amount,Merchant_Category,Timestamp\n';
  hits.forEach(hit => {
    const t = hit._source || {};
    reportContent += `"${t.transaction_id || ''}","${t.customer_id || ''}",${t.amount || 0},"${t.merchant_category || ''}","${t.timestamp || ''}"\n`;
  });
} else {
  // Text format
  reportContent = `DYNAMIC SEARCH REPORT\n`;
  reportContent += `======================\n\n`;
  reportContent += `Search Criteria:\n`;
  reportContent += `- Minimum Amount: $${searchParams.minAmount}\n`;
  reportContent += `- Time Range: ${searchParams.timeRange}\n`;
  reportContent += `- Customer: ${searchParams.customerId || 'All'}\n\n`;
  reportContent += `Results: ${totalFound} transactions found\n\n`;

  if (hits.length > 0) {
    reportContent += `TRANSACTIONS:\n`;
    reportContent += `=============\n\n`;
    hits.forEach((hit, index) => {
      const t = hit._source || {};
      reportContent += `${index + 1}. Transaction ID: ${t.transaction_id}\n`;
      reportContent += `   Customer: ${t.customer_id}\n`;
      reportContent += `   Amount: $${t.amount}\n`;
      reportContent += `   Merchant: ${t.merchant_category}\n`;
      reportContent += `   Time: ${t.timestamp}\n\n`;
    });
  } else {
    reportContent += `No suspicious transactions found matching your criteria.\n`;
  }
}

// Convert content to binary data
const binaryData = Buffer.from(reportContent, 'utf8');

return {
  json: {
    filename: filename,
    mimeType: mimeType,
    content: reportContent
  },
  binary: {
    data: binaryData
  }
};
