additionalFields.outputSchema:
{
  "type": "object",
  "properties": {
    "posts": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "post_text": {
            "type": "string",
            "description": "The text content of the post."
          },
          "post_url": {
            "type": "string",
            "description": "The URL of the post."
          },
          "page_profile_url": {
            "type": "string",
            "description": "The URL of the page or profile that made the post."
          },
          "timestamp": {
            "type": "string",
            "description": "The timestamp of when the post was made."
          },
          "number_of_likes": {
            "type": "integer",
            "description": "The number of likes the post has received."
          },
          "number_of_shares": {
            "type": "integer",
            "description": "The number of times the post has been shared."
          },
          "number_of_comments": {
            "type": "integer",
            "description": "The number of comments on the post."
          },
          "page_profile_details": {
            "type": "string",
            "description": "Details about the page or profile that made the post."
          },
          "post_thumbnail": {
            "type": "string",
            "description": "The URL of the post's thumbnail image."
          }
        },
        "required": [
          "post_text",
          "post_url",
          "page_profile_url",
          "timestamp",
          "number_of_likes",
          "number_of_shares",
          "number_of_comments",
          "page_profile_details",
          "post_thumbnail"
        ],
        "additionalProperties": false
      }
    }
  },
  "required": [
    "posts"
  ],
  "additionalProperties": false,
  "$schema": "http://json-schema.org/draft-07/schema#"
}
