// Process approval/rejection action
const webhookData = items[0].json;

// Extract data from the webhook payload
const body = webhookData.body || webhookData;
const action = body.action;
const user = body.user || {};
const timestamp = body.timestamp || Date.now();

// Create response based on action
let message = '';
let emoji = '';
let status = '';

const userDisplayName = user.name || user.id || 'Unknown User';

if (action === 'approve') {
  emoji = '✅';
  status = 'APPROVED';
  message = `${emoji} *Request ${status}*\n\n` +
           `👤 *Approved by:* ${userDisplayName}\n` +
           `⏰ *Time:* ${new Date(timestamp).toLocaleString()}\n` +
           `🎯 *Action:* Request has been processed successfully and approved for execution.\n` +
           `📋 *Next Steps:* The automation workflow will proceed with the approved action.`;
} else if (action === 'reject') {
  emoji = '❌';
  status = 'REJECTED';
  message = `${emoji} *Request ${status}*\n\n` +
           `👤 *Rejected by:* ${userDisplayName}\n` +
           `⏰ *Time:* ${new Date(timestamp).toLocaleString()}\n` +
           `🚫 *Action:* Request has been rejected and will not be processed.\n` +
           `📋 *Next Steps:* No further action will be taken for this request.`;
} else {
  emoji = '⚠️';
  status = 'UNKNOWN';
  message = `${emoji} *Unknown Action Received*\n\n` +
           `👤 *User:* ${userDisplayName}\n` +
           `⏰ *Time:* ${new Date(timestamp).toLocaleString()}\n` +
           `❓ *Action:* ${action || 'undefined'}\n` +
           `📋 *Status:* Unable to process this action type.`;
}

// Return processed data
return [{
  json: {
    ...webhookData,
    processedMessage: message,
    emoji: emoji,
    status: status,
    processedAt: new Date().toISOString()
  }
}];
