/**
 *  Gelen JSON’da URL olabilecek alanları buraya yaz.
 *  Telegram mesajı => "text"
 *  İstersen diğer adlar da kalsın.
 */
const urlFields = ['text', 'url', 'link', 'address'];

items.forEach(item => {
  urlFields.forEach(field => {
    const original = item.json[field];
    if (typeof original !== 'string' || original.trim() === '') return;

    // http:// veya https:// ile başlıyor mu?
    const hasScheme = /^(https?:\/\/)/i.test(original);

    // Yoksa başa https:// ekle
    if (!hasScheme) {
      item.json[field] = `https://${original}`;
    }
  });
});

return items;

