// 🎲 Level 1: Data Warrior Challenge
// Challenge: Deduplicate corrupted user database

const gameData = items[0].json;
const player = gameData.player;

// Corrupted database with duplicates (the challenge data)
const corruptedUsers = [
  { id: 1, name: "Knight Arthur", email: "arthur@camelot.com", class: "Warrior" },
  { id: 2, name: "Wizard Merlin", email: "merlin@magic.com", class: "Mage" },
  { id: 3, name: "Knight Arthur", email: "arthur@camelot.com", class: "Warrior" }, // Duplicate
  { id: 4, name: "Archer Robin", email: "robin@forest.com", class: "Ranger" },
  { id: 5, name: "Rogue Shadow", email: "shadow@dark.com", class: "Assassin" },
  { id: 6, name: "Wizard Merlin", email: "merlin@magic.com", class: "Mage" }, // Duplicate
  { id: 7, name: "Paladin Grace", email: "grace@light.com", class: "Healer" },
  { id: 8, name: "Archer Robin", email: "robin@forest.com", class: "Ranger" } // Duplicate
];

// THE CHALLENGE: Deduplicate based on email (player must implement this logic)
// This is the solution - in a real game, the player would need to write this
const cleanedUsers = corruptedUsers.filter(
  (user, index, self) => 
    index === self.findIndex(u => u.email === user.email)
);

// Calculate challenge results
const originalCount = corruptedUsers.length;
const cleanedCount = cleanedUsers.length;
const duplicatesRemoved = originalCount - cleanedCount;

// Determine success and rewards
const isSuccess = duplicatesRemoved > 0;
const xpGained = isSuccess ? 100 : 0;
const powerUp = isSuccess ? "Data Shield 🛡️" : null;

// Update player stats
if (isSuccess) {
  player.xp += xpGained;
  player.level = Math.floor(player.xp / 100) + 1;
  if (powerUp) player.inventory.push(powerUp);
  player.achievements.push("Data Warrior");
}

const challengeResult = {
  level: 1,
  challengeName: "Data Warrior Challenge",
  success: isSuccess,
  stats: {
    originalRecords: originalCount,
    cleanedRecords: cleanedCount,
    duplicatesRemoved: duplicatesRemoved
  },
  rewards: {
    xp: xpGained,
    powerUp: powerUp
  },
  message: isSuccess ? 
    `🎉 SUCCESS! You've cleaned the corrupted database and earned ${xpGained} XP!` :
    "💀 FAILED! The database is still corrupted. Try again!"
};

return [{
  json: {
    ...challengeResult,
    player,
    cleanedData: cleanedUsers,
    nextLevel: isSuccess ? 2 : 1
  }
}];

// ⚔️ Level 2: API Ninja Challenge
// Challenge: Transform and validate API responses

const gameData = items[0].json;
const player = gameData.player;

// Mock API response with messy data (the challenge)
const apiResponse = {
  users: [
    { id: "1", full_name: "  John Doe  ", email_address: "JOHN@EXAMPLE.COM", age: "25", status: "active" },
    { id: "2", full_name: "jane smith", email_address: "jane@example.com", age: "invalid", status: "INACTIVE" },
    { id: "3", full_name: "Bob Johnson", email_address: "bob@test.com", age: "30", status: "active" },
    { id: "4", full_name: "", email_address: "empty@test.com", age: "22", status: "pending" },
    { id: "5", full_name: "Alice Brown", email_address: "alice@domain.com", age: "28", status: "active" }
  ]
};

// THE CHALLENGE: Transform and validate the data
// This is the solution - in a real game, player would implement this
const transformedUsers = apiResponse.users.map(user => {
  const transformed = {
    id: parseInt(user.id),
    name: user.full_name.trim(),
    email: user.email_address.toLowerCase(),
    age: parseInt(user.age) || 0,
    status: user.status.toLowerCase(),
    isValid: true
  };

  // Validation rules
  if (!transformed.name || transformed.name.length < 2) {
    transformed.isValid = false;
    transformed.errors = (transformed.errors || []).concat(['Invalid name']);
  }

  if (!transformed.email.includes('@')) {
    transformed.isValid = false;
    transformed.errors = (transformed.errors || []).concat(['Invalid email']);
  }

  if (transformed.age < 18 || transformed.age > 120) {
    transformed.isValid = false;
    transformed.errors = (transformed.errors || []).concat(['Invalid age']);
  }

  return transformed;
});

// Calculate challenge results
const validUsers = transformedUsers.filter(user => user.isValid);
const invalidUsers = transformedUsers.filter(user => !user.isValid);

const isSuccess = validUsers.length >= 3; // Need at least 3 valid users to pass
const xpGained = isSuccess ? 200 : 0;
const powerUp = isSuccess ? "Speed Boost ⚡" : null;

// Update player stats
if (isSuccess) {
  player.xp += xpGained;
  player.level = Math.floor(player.xp / 100) + 1;
  if (powerUp) player.inventory.push(powerUp);
  player.achievements.push("API Ninja");
}

const challengeResult = {
  level: 2,
  challengeName: "API Ninja Challenge",
  success: isSuccess,
  stats: {
    totalUsers: transformedUsers.length,
    validUsers: validUsers.length,
    invalidUsers: invalidUsers.length,
    transformationAccuracy: Math.round((validUsers.length / transformedUsers.length) * 100)
  },
  rewards: {
    xp: xpGained,
    powerUp: powerUp
  },
  message: isSuccess ? 
    `⚡ SUCCESS! You've mastered data transformation and earned ${xpGained} XP!` :
    "🥷 FAILED! Your ninja skills need more practice. Try again!"
};

return [{
  json: {
    ...challengeResult,
    player,
    validUsers,
    invalidUsers,
    nextLevel: isSuccess ? 3 : 2
  }
}];

// 🏆 Final Boss: Automation Master Challenge
// Challenge: Build a complete workflow system

const gameData = items[0].json;
const player = gameData.player;

// Complex workflow challenge data
const workflowChallenge = {
  tasks: [
    { id: 1, name: "Data Ingestion", status: "pending", priority: "high", assignee: "system" },
    { id: 2, name: "Data Validation", status: "pending", priority: "high", assignee: "validator" },
    { id: 3, name: "Data Transformation", status: "pending", priority: "medium", assignee: "transformer" },
    { id: 4, name: "Error Handling", status: "pending", priority: "high", assignee: "error_handler" },
    { id: 5, name: "Data Output", status: "pending", priority: "low", assignee: "output" },
    { id: 6, name: "Notification", status: "pending", priority: "medium", assignee: "notifier" }
  ],
  workflow: {
    name: "Master Automation Pipeline",
    steps: 6,
    complexity: "Expert",
    timeLimit: "5 minutes"
  }
};

// THE FINAL CHALLENGE: Process tasks in correct order with error handling
// This is the solution - the ultimate test of n8n skills
const processWorkflow = (tasks) => {
  const results = [];
  const errors = [];

  try {
    // Step 1: Sort by priority (high -> medium -> low)
    const priorityOrder = { 'high': 3, 'medium': 2, 'low': 1 };
    const sortedTasks = tasks.sort((a, b) => priorityOrder[b.priority] - priorityOrder[a.priority]);

    // Step 2: Process each task with error handling
    sortedTasks.forEach(task => {
      try {
        // Simulate processing
        const processed = {
          ...task,
          status: 'completed',
          completedAt: new Date().toISOString(),
          processingTime: Math.random() * 1000 + 500 // Random processing time
        };

        // Add some business logic
        if (task.name.includes('Error')) {
          processed.errorHandlingActive = true;
        }

        if (task.priority === 'high') {
          processed.fastTrack = true;
        }

        results.push(processed);
      } catch (error) {
        errors.push({
          taskId: task.id,
          error: error.message,
          timestamp: new Date().toISOString()
        });
      }
    });

    // Step 3: Generate workflow summary
    const summary = {
      totalTasks: tasks.length,
      completedTasks: results.length,
      failedTasks: errors.length,
      highPriorityTasks: results.filter(t => t.priority === 'high').length,
      averageProcessingTime: Math.round(results.reduce((sum, t) => sum + t.processingTime, 0) / results.length),
      workflowEfficiency: Math.round((results.length / tasks.length) * 100)
    };

    return { results, errors, summary };
  } catch (error) {
    return { results: [], errors: [{ general: error.message }], summary: null };
  }
};

// Execute the workflow
const workflowResult = processWorkflow(workflowChallenge.tasks);

// Determine success (need 100% completion and efficiency > 80%)
const isSuccess = workflowResult.summary && 
                  workflowResult.summary.completedTasks === workflowChallenge.tasks.length &&
                  workflowResult.summary.workflowEfficiency >= 80;

const xpGained = isSuccess ? 500 : 0;
const powerUp = isSuccess ? "Master Badge 🏅" : null;
const finalTitle = isSuccess ? "🏆 AUTOMATION MASTER" : "🎯 Aspiring Automator";

// Update player stats for final time
if (isSuccess) {
  player.xp += xpGained;
  player.level = Math.floor(player.xp / 100) + 1;
  if (powerUp) player.inventory.push(powerUp);
  player.achievements.push("Automation Master");
  player.title = finalTitle;
}

// Calculate final game score
const finalScore = player.xp + (player.achievements.length * 50);

const bossResult = {
  level: 3,
  challengeName: "Automation Master Boss Battle",
  success: isSuccess,
  workflowResult,
  rewards: {
    xp: xpGained,
    powerUp: powerUp,
    title: finalTitle
  },
  finalStats: {
    totalXP: player.xp,
    finalLevel: player.level,
    achievements: player.achievements.length,
    finalScore: finalScore,
    inventory: player.inventory
  },
  message: isSuccess ? 
    `🎉 VICTORY! You've become the ultimate Automation Master with ${finalScore} points!` :
    "⚔️ The boss was too powerful! Train more and try again!"
};

return [{
  json: {
    ...bossResult,
    player,
    gameComplete: isSuccess,
    playAgain: "Click 'Start Game' to play again!"
  }
}];

// 🎊 Game Over Screen
// Display final results and achievements

const gameData = items[0].json;
const player = gameData.player;

// Create achievement showcase
const achievementShowcase = {
  playerName: player.name,
  finalTitle: player.title || "n8n Apprentice",
  totalXP: player.xp,
  finalLevel: player.level,
  achievements: player.achievements,
  inventory: player.inventory,
  gameComplete: gameData.gameComplete
};

// Generate performance report
const performanceReport = {
  levelsCompleted: player.achievements.length,
  totalChallenges: 3,
  completionRate: Math.round((player.achievements.length / 3) * 100),
  skillsLearned: [
    "Data Deduplication",
    "API Response Transformation",
    "Workflow Automation",
    "Error Handling",
    "JavaScript Array Methods",
    "n8n Code Node Mastery"
  ],
  nextSteps: [
    "Explore advanced n8n nodes",
    "Build real-world automations",
    "Learn webhook integrations",
    "Master database operations",
    "Implement error handling patterns"
  ]
};

// Create motivational message based on performance
let motivationalMessage = "";
if (gameData.gameComplete) {
  motivationalMessage = `🌟 Congratulations! You've mastered the art of automation and proven yourself worthy of the title '${player.title}'. Your journey in n8n has just begun!`;
} else {
  motivationalMessage = `🎯 Great effort! You've learned valuable skills and earned ${player.xp} XP. Keep practicing to become the ultimate Automation Master!`;
}

// Special rewards based on performance
const specialRewards = [];
if (player.xp >= 800) specialRewards.push("🏆 Perfect Score Bonus");
if (player.achievements.includes("Data Warrior")) specialRewards.push("🛡️ Data Protection Expert");
if (player.achievements.includes("API Ninja")) specialRewards.push("⚡ Speed Optimization Master");
if (player.achievements.includes("Automation Master")) specialRewards.push("🎖️ Workflow Architect");

// Create final game summary
const gameSummary = {
  title: "🎮 Code Challenge Adventure - Game Over!",
  subtitle: `Final Score: ${gameData.finalStats ? gameData.finalStats.finalScore : player.xp} Points`,
  achievementShowcase,
  performanceReport,
  specialRewards,
  motivationalMessage,
  coachingOffer: {
    message: "Want to level up your n8n skills in real life?",
    coaching: "👉 Book a coaching session: david@daexai.com",
    consulting: "📩 Get custom automation help: david@daexai.com"
  },
  playAgain: "🔄 Ready for another adventure? Click 'Start Game' to play again!"
};

return [{
  json: gameSummary
}];

