// Get the posts array from Agent output
const posts = $json.output;

// Initialize counters
let totalLikes = 0;
let totalComments = 0;

// Loop through posts and sum likes and comments
for (const post of posts) {
  totalLikes += post.likes;
  totalComments += post.comments;
}

// Calculate averages
const averageLikes = totalLikes / posts.length;
const averageComments = totalComments / posts.length;

// Return summary object
return [
  {
    json: {
      totalPosts: posts.length,
      totalLikes: totalLikes,
      totalComments: totalComments,
      averageLikes: averageLikes,
      averageComments: averageComments
    }
  }
];

// Get the Agent output (array of posts)
const posts = $('🤖 Agent: Fetch LinkedIn Posts (via MCP Tool)').first().json.output;

// Map each post to its own item
return posts.map(post => {
  return {
    json: post
  };
});
