// Aggregate all supplier data and calculate risk scores
const supplierData = $input.all();
const riskThresholds = {
  low: 3,
  medium: 6,
  high: 8
};

function calculateOverallRisk(supplier) {
  let riskScore = 0;

  // Financial status scoring
  if (supplier.financial_status === 'critical') riskScore += 4;
  else if (supplier.financial_status === 'warning') riskScore += 2;

  // Operational issues scoring
  const operationalIssues = supplier.operational_issues || [];
  riskScore += Math.min(operationalIssues.length, 3);

  // Regulatory problems scoring
  const regulatoryProblems = supplier.regulatory_problems || [];
  riskScore += Math.min(regulatoryProblems.length * 1.5, 3);

  // News sentiment scoring
  if (supplier.news_sentiment === 'negative') riskScore += 2;
  else if (supplier.news_sentiment === 'neutral') riskScore += 0.5;

  return Math.min(Math.round(riskScore), 10);
}

function getRiskLevel(score) {
  if (score >= riskThresholds.high) return 'HIGH';
  if (score >= riskThresholds.medium) return 'MEDIUM';
  return 'LOW';
}

function formatRiskReport(suppliers) {
  const highRiskSuppliers = suppliers.filter(function(s) { return s.overall_risk_score >= riskThresholds.high; });
  const mediumRiskSuppliers = suppliers.filter(function(s) { return s.overall_risk_score >= riskThresholds.medium && s.overall_risk_score < riskThresholds.high; });

  let report = '🚨 **SUPPLY CHAIN RISK REPORT**\n📅 Date: ' + new Date().toLocaleDateString() + '\n\n';

  if (highRiskSuppliers.length > 0) {
    report += '⚠️ **HIGH RISK SUPPLIERS (' + highRiskSuppliers.length + ')**\n';
    for (let i = 0; i < highRiskSuppliers.length; i++) {
      const supplier = highRiskSuppliers[i];
      const operationalCount = (supplier.operational_issues || []).length;
      report += '• ' + supplier.supplier_name + ' (Score: ' + supplier.overall_risk_score + '/10)\n';
      report += '  Status: ' + supplier.financial_status + ' | Issues: ' + operationalCount + '\n';
    }
    report += '\n';
  }

  if (mediumRiskSuppliers.length > 0) {
    report += '⚡ **MEDIUM RISK SUPPLIERS (' + mediumRiskSuppliers.length + ')**\n';
    for (let i = 0; i < mediumRiskSuppliers.length; i++) {
      const supplier = mediumRiskSuppliers[i];
      report += '• ' + supplier.supplier_name + ' (Score: ' + supplier.overall_risk_score + '/10)\n';
    }
    report += '\n';
  }

  report += '📊 **SUMMARY**\n';
  report += 'Total Suppliers Monitored: ' + suppliers.length + '\n';
  report += 'High Risk: ' + highRiskSuppliers.length + '\n';
  report += 'Medium Risk: ' + mediumRiskSuppliers.length + '\n';
  report += 'Low Risk: ' + (suppliers.length - highRiskSuppliers.length - mediumRiskSuppliers.length) + '\n';

  return report;
}

// Process each supplier's data
const processedSuppliers = [];
for (let i = 0; i < supplierData.length; i++) {
  const item = supplierData[i];
  const supplier = item.json.result || item.json;
  const overallRiskScore = calculateOverallRisk(supplier);
  const riskLevel = getRiskLevel(overallRiskScore);

  processedSuppliers.push({
    supplier_name: supplier.supplier_name || 'Unknown Supplier',
    financial_status: supplier.financial_status || 'stable',
    operational_issues: supplier.operational_issues || [],
    regulatory_problems: supplier.regulatory_problems || [],
    news_sentiment: supplier.news_sentiment || 'neutral',
    overall_risk_score: overallRiskScore,
    risk_level: riskLevel,
    needs_attention: overallRiskScore >= riskThresholds.medium,
    assessment_date: new Date().toISOString(),
    source_url: supplier.source_url || ''
  });
}

// Generate summary report
const summaryReport = formatRiskReport(processedSuppliers);

console.log('Processed ' + processedSuppliers.length + ' suppliers');
const highRiskCount = processedSuppliers.filter(function(s) { return s.risk_level === 'HIGH'; }).length;
console.log('High risk suppliers: ' + highRiskCount);

return [{
  json: {
    suppliers: processedSuppliers,
    summary_report: summaryReport,
    high_risk_count: highRiskCount,
    medium_risk_count: processedSuppliers.filter(function(s) { return s.risk_level === 'MEDIUM'; }).length,
    total_suppliers: processedSuppliers.length,
    assessment_timestamp: new Date().toISOString()
  }
}];
