// This code works when "Execute Once" is ON.
// 'items' is an array of all customers from the previous node.

for (const item of items) {
  const customer = item.json;

  // --- 1. Dynamic Personalization Logic ---
  const greeting = customer.GivenName ? `Hello ${customer.GivenName},` : `Hello ${customer.DisplayName},`;
  let openingLine = `We are absolutely thrilled to welcome you to our family!`;
  if (customer.CompanyName) {
    openingLine = `We are absolutely thrilled to welcome <strong>${customer.CompanyName}</strong> to our family!`;
  }
  const detailsConfirmation = `
    <div style="background-color: #f8f9fa; padding: 15px; border-radius: 5px; margin-top: 15px; border: 1px solid #dee2e6;">
      <strong>Display Name:</strong> ${customer.DisplayName}<br>
      <strong>Email:</strong> ${customer.PrimaryEmailAddr.Address}
      ${customer.BillAddr && customer.BillAddr.City ? `<br><strong>Location:</strong> ${customer.BillAddr.City}, ${customer.BillAddr.CountrySubDivisionCode}` : ''}
    </div>
  `;

  // --- 2. Construct the HTML Email Body ---
  const emailHtml = `
  <!DOCTYPE html>
  <html>
  <head>
    <title>A Warm Welcome!</title>
  </head>
  <body style="margin: 0; padding: 0; background-color: #f4f4f4; font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif;">
    <table border="0" cellpadding="0" cellspacing="0" width="100%">
      <tr>
        <td style="padding: 20px 0;">
          <table align="center" border="0" cellpadding="0" cellspacing="0" width="600" style="border-collapse: collapse; background-color: #ffffff; max-width: 600px;">
            <tr>
              <td align="center" style="padding: 30px; background-color: #00466a;">
                <img src="https://yourdomain.com/images/logo.png" alt="Company Logo" style="display: block; width: 150px;">
              </td>
            </tr>
            <tr>
              <td style="padding: 40px 30px;">
                <h1 style="font-size: 24px; font-weight: bold; color: #153643;">${greeting}</h1>
                <p style="font-size: 16px; line-height: 1.5; color: #333333;">${openingLine}</p>
                <p style="font-size: 16px; line-height: 1.5; color: #333333;">
                  To ensure everything is perfect from the start, we've listed the primary details we have on file for you:
                </p>
                ${detailsConfirmation}
                <table border="0" cellpadding="0" cellspacing="0" width="100%">
                  <tr>
                    <td align="center" style="padding: 20px 0;">
                      <a href="https://yourdomain.com/" style="background-color: #ff7f50; color: #ffffff; padding: 15px 25px; text-decoration: none; border-radius: 5px; font-size: 16px; display: inline-block;">Explore Your Dashboard</a>
                    </td>
                  </tr>
                </table>
              </td>
            </tr>
            <tr>
              <td style="padding: 30px; background-color: #eeeeee; color: #555555; font-size: 14px; text-align: center;">
                  <p style="margin: 0;">Questions? Just reply to this email or <a href="mailto:info@yourdomain.com" style="color: #00466a;">contact our support team</a>.</p>
                  <p style="margin: 10px 0 0 0;">© 2025 [Your Company Name], All rights reserved.</p>
              </td>
            </tr>
          </table>
        </td>
      </tr>
    </table>
  </body>
  </html>
  `;

  // --- 3. Add the generated HTML to the current item ---
  item.json.emailHtml = emailHtml;
}

// --- 4. Return all the modified items for the next node ---
return items;
