// Extract conversion data from Google search results
const htmlContent = $input.first().json.data;
const query = $('Webhook').first().json.query.q;

// Parse the query to extract amount, from currency, and to currency
const queryMatch = query.match(/(\d+(?:\.\d+)?)\s*([a-zA-Z]{3})\s+to\s+([a-zA-Z]{3})/i);

if (!queryMatch) {
  return [{
    json: {
      error: 'Invalid query format. Please use format like: 5usd to mxn',
      originalQuery: query
    }
  }];
}

const amount = parseFloat(queryMatch[1]);
const fromCurrency = queryMatch[2].toUpperCase();
const toCurrency = queryMatch[3].toUpperCase();

// Try to extract exchange rate from various possible selectors
let exchangeRate = null;
let convertedAmount = null;

// Common patterns in Google search results
const patterns = [
  // Pattern 1: Direct rate extraction
  /([0-9,.]+)\s*([A-Z]{3})\s*=\s*([0-9,.]+)\s*([A-Z]{3})/g,
  // Pattern 2: Rate in format "1 USD = X MXN"
  /1\s*([A-Z]{3})\s*=\s*([0-9,.]+)\s*([A-Z]{3})/g,
  // Pattern 3: Direct conversion result
  new RegExp(`${amount}\\s*${fromCurrency}\\s*=\\s*([0-9,.]+)\\s*${toCurrency}`, 'i')
];

// Try to find exchange rate information
for (const pattern of patterns) {
  const matches = [...htmlContent.matchAll(pattern)];
  if (matches.length > 0) {
    const match = matches[0];
    if (pattern === patterns[2]) {
      // Direct conversion result
      convertedAmount = parseFloat(match[1].replace(/,/g, ''));
      exchangeRate = convertedAmount / amount;
      break;
    } else {
      // Extract rate from other patterns
      const rateValue = parseFloat(match[2].replace(/,/g, ''));
      if (match[1] === fromCurrency && match[3] === toCurrency) {
        exchangeRate = rateValue;
      } else if (match[3] === fromCurrency && match[1] === toCurrency) {
        exchangeRate = 1 / rateValue;
      }

      if (exchangeRate) {
        convertedAmount = amount * exchangeRate;
        break;
      }
    }
  }
}

// Fallback: Try to find any number that could be the conversion result
if (!convertedAmount) {
  const numberPattern = new RegExp(`${amount}[^0-9]*([0-9,.]+)`, 'g');
  const numberMatches = [...htmlContent.matchAll(numberPattern)];

  if (numberMatches.length > 0) {
    convertedAmount = parseFloat(numberMatches[0][1].replace(/,/g, ''));
    exchangeRate = convertedAmount / amount;
  }
}

// If still no result, provide a fallback
if (!convertedAmount) {
  return [{
    json: {
      error: 'Could not extract exchange rate from search results',
      originalQuery: query,
      suggestion: 'Please try a different currency pair or check the format'
    }
  }];
}

return [{
  json: {
    originalQuery: query,
    amount: amount,
    fromCurrency: fromCurrency,
    toCurrency: toCurrency,
    exchangeRate: exchangeRate,
    convertedAmount: convertedAmount,
    timestamp: new Date().toISOString()
  }
}];

// Format the currency response
const data = $input.first().json;

if (data.error) {
  return [{
    json: {
      response: `Error: ${data.error}`,
      statusCode: 400
    }
  }];
}

// Format the conversion result
const formattedResult = `${data.amount} ${data.fromCurrency} = ${data.convertedAmount.toFixed(2)} ${data.toCurrency}`;

// Additional metadata
const responseData = {
  result: formattedResult,
  details: {
    amount: data.amount,
    fromCurrency: data.fromCurrency,
    toCurrency: data.toCurrency,
    exchangeRate: data.exchangeRate ? data.exchangeRate.toFixed(6) : null,
    convertedAmount: data.convertedAmount.toFixed(2),
    timestamp: data.timestamp
  },
  query: data.originalQuery
};

return [{
  json: {
    response: formattedResult,
    data: responseData,
    statusCode: 200
  }
}];
