parameters.jsonBody:
={
    "query": "mutation ($input: MoneyTransactionCreateInput!) { moneyTransactionCreate(input: $input) { didSucceed inputErrors { path message code } transaction { id } } }",
    "variables": {
      "input": {
        "businessId": "BUSINESS ID HERE",
        "externalId": "stripe-payout-{{ $json.created.toDateTime('s').format('yyyy-MM-dd') }}",
        "date": "{{ $json.created.toDateTime('s').format('yyyy-MM-dd') }}",
        "description": "Payout from Stripe - ${{ ($json.data.object.amount / 100).toFixed(2) }} total",
        "anchor": {
          "accountId": "ANCHOR ACCOUNT ID HERE",
          "amount": {{ ($json.data.object.amount / 100).toFixed(2) }},
          "direction": "DEPOSIT"
        },
        "lineItems": [
          {
            "accountId": "DEPOSIT ACCOUNT ID HERE",
            "amount": {{ ($json.data.object.amount / 100).toFixed(2) }},
            "balance": "INCREASE",
            "taxes": [
              {
                "salesTaxId": "SALES TAX ACCOUNT ID HERE - EXAMPLE BELOW IS 5% TAX BAKED INTO TOTAL AMOUNT",
                "amount": {{ (($json.data.object.amount / 100) - (($json.data.object.amount / 100) / 1.05)).toFixed(2) }}
              }
            ]
          }
        ]
      }
    }
}
