// This node extracts and formats the appointment data from the incoming webhook.
// The expected webhook payload from your booking software should look something like this:
// {
//   "customer_name": "John Doe",
//   "customer_phone": "+14155552671",
//   "appointment_id": "appt_12345",
//   "appointment_service": "Consultation",
//   "appointment_time": "2025-08-04 14:00:00"
// }

const webhookData = $input.item.json.body || {};

// Basic data extraction and validation
const customerName = webhookData.customer_name || 'Valued Customer';
const customerPhone = webhookData.customer_phone;
const appointmentTime = webhookData.appointment_time || 'today';
const serviceName = webhookData.appointment_service || 'your appointment';
const appointmentId = webhookData.appointment_id || 'no-id-provided';

if (!customerPhone) {
  throw new Error('No customer phone number found in webhook data');
}

// Format the appointment time for the SMS message
// You can customize the date formatting here if needed.
const formattedTime = new Date(appointmentTime).toLocaleString('en-US', {
  weekday: 'short',
  month: 'short',
  day: 'numeric',
  hour: 'numeric',
  minute: 'numeric'
});

return {
  json: {
    customer_name: customerName,
    customer_phone: customerPhone,
    appointment_time: formattedTime,
    service_name: serviceName,
    appointment_id: appointmentId,
    raw_data: webhookData
  }
};
