// Extract PDF content and prepare for AI analysis
const emailData = items[0].json;
let pdfContent = '';

// Check for attachments
if (emailData.attachments && emailData.attachments.length > 0) {
  const pdfAttachment = emailData.attachments.find(att => 
    att.contentType === 'application/pdf' || 
    att.filename?.toLowerCase().includes('.pdf')
  );

  if (pdfAttachment) {
    // In a real scenario, you'd use a PDF parser here
    // For now, we'll use the email text content as a fallback
    pdfContent = emailData.textPlain || emailData.html || 'PDF content extraction needed';
  }
} else {
  pdfContent = emailData.textPlain || emailData.html || '';
}

// Extract patient email from sender
const patientEmail = emailData.from?.value?.[0]?.address || emailData.from;

return [{
  json: {
    patient_email: patientEmail,
    report_content: pdfContent,
    subject: emailData.subject,
    received_date: new Date().toISOString()
  }
}];
