const attendanceRecords = $('Read Today\'s Attendance').all();
const studentContacts = $('Read Student Contacts').all();

const absentStudents = [];
const today = new Date().toISOString().split('T')[0];

for (const record of attendanceRecords) {
  const attendance = record.json;

  if (attendance.status === 'Absent' && !attendance.excused) {
    const student = studentContacts.find(s => s.json.studentId === attendance.studentId);

    if (student) {
      const studentData = student.json;

      // Calculate recent absence pattern (last 30 days)
      const recentAbsences = attendanceRecords.filter(r => {
        const recordDate = new Date(r.json.date);
        const thirtyDaysAgo = new Date();
        thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
        return r.json.studentId === attendance.studentId && 
               r.json.status === 'Absent' && 
               recordDate >= thirtyDaysAgo;
      }).length;

      absentStudents.push({
        studentId: attendance.studentId,
        studentName: studentData.studentName,
        className: studentData.className,
        parentName: studentData.parentName,
        parentEmail: studentData.parentEmail,
        parentPhone: studentData.parentPhone,
        absentDate: today,
        recentAbsences: recentAbsences,
        attendanceRate: Math.round(((30 - recentAbsences) / 30) * 100),
        alertLevel: recentAbsences >= 5 ? 'High' : recentAbsences >= 3 ? 'Medium' : 'Low'
      });
    }
  }
}

return absentStudents.map(item => ({ json: item }));

const studentData = $input.first().json;

const alertIcon = studentData.alertLevel === 'High' ? '🚨' : studentData.alertLevel === 'Medium' ? '⚠️' : 'ℹ️';

const emailSubject = `${alertIcon} Absence Alert: ${studentData.studentName} - ${studentData.absentDate}`;
const emailBody = `Dear ${studentData.parentName},

We are writing to inform you that ${studentData.studentName} was marked absent today (${studentData.absentDate}).

Attendance Summary:
- Student: ${studentData.studentName}
- Class: ${studentData.className}
- Absent Date: ${studentData.absentDate}
- Recent Absences (30 days): ${studentData.recentAbsences}
- Attendance Rate: ${studentData.attendanceRate}%
- Alert Level: ${studentData.alertLevel}

${studentData.alertLevel === 'High' ? 
  '⚠️ ATTENTION: Your child has exceeded the acceptable absence limit. Please contact the school office immediately to discuss this matter.' :
  studentData.alertLevel === 'Medium' ? 
  '📋 NOTICE: Your child\'s attendance needs attention. Please ensure regular attendance to avoid academic impact.' :
  '📝 INFORMATION: This is a routine absence notification. Please ensure your child attends school regularly.'}

If this absence was due to illness or other valid reason, please contact the school office with appropriate documentation.

For attendance concerns, contact: attendance@school.edu or (555) 123-4567

Best regards,
School Attendance Office`;

return [{
  json: {
    to: studentData.parentEmail,
    subject: emailSubject,
    body: emailBody,
    studentName: studentData.studentName,
    parentName: studentData.parentName,
    alertLevel: studentData.alertLevel
  }
}];

const studentData = $input.first().json;

const alertEmoji = studentData.alertLevel === 'High' ? '🚨' : studentData.alertLevel === 'Medium' ? '⚠️' : '📋';

const whatsappMessage = `${alertEmoji} *Absence Alert*\n\nDear ${studentData.parentName},\n\n👤 Student: *${studentData.studentName}*\n🏫 Class: ${studentData.className}\n📅 Absent Date: ${studentData.absentDate}\n\n📊 *Attendance Summary:*\n• Recent Absences (30 days): ${studentData.recentAbsences}\n• Attendance Rate: ${studentData.attendanceRate}%\n• Alert Level: *${studentData.alertLevel}*\n\n${studentData.alertLevel === 'High' ? 
  '🚨 *URGENT:* Please contact school office immediately regarding excessive absences.' :
  studentData.alertLevel === 'Medium' ? 
  '⚠️ *ATTENTION:* Please ensure regular attendance to avoid academic impact.' :
  '📝 *NOTICE:* Routine absence notification. Please ensure regular attendance.'}

If excused, please contact school office with documentation.\n\n📞 Contact: (555) 123-4567\n📧 attendance@school.edu\n\n_School Attendance Office_`;

return [{
  json: {
    phone: studentData.parentPhone,
    message: whatsappMessage,
    studentName: studentData.studentName,
    parentName: studentData.parentName,
    alertLevel: studentData.alertLevel
  }
}];

const absentStudents = $input.all();
const today = new Date().toISOString().split('T')[0];

const attendanceReport = {
  date: today,
  totalAbsences: absentStudents.length,
  highAlerts: absentStudents.filter(s => s.json.alertLevel === 'High').length,
  mediumAlerts: absentStudents.filter(s => s.json.alertLevel === 'Medium').length,
  lowAlerts: absentStudents.filter(s => s.json.alertLevel === 'Low').length,
  averageAttendanceRate: Math.round(
    absentStudents.reduce((sum, s) => sum + s.json.attendanceRate, 0) / absentStudents.length
  ),
  studentsAtRisk: absentStudents.filter(s => s.json.recentAbsences >= 5).map(s => ({
    studentName: s.json.studentName,
    className: s.json.className,
    recentAbsences: s.json.recentAbsences,
    attendanceRate: s.json.attendanceRate
  }))
};

return [{ json: attendanceReport }];
